import React, { useState } from "react";
import UploadForm from "./components/UploadForm";
import DocumentList from "./components/DocumentList";
import DocumentView from "./components/DocumentView";

export default function App() {
  const [selectedId, setSelectedId] = useState(null);

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <aside className="w-80 bg-white shadow-lg p-5 flex flex-col">
        <div className="text-2xl font-bold mb-6 text-blue-600">
          ðŸ“„ AI Document Dashboard
        </div>

        <UploadForm onUploaded={() => window.location.reload()} />

        <div className="mt-6 flex-grow overflow-y-auto">
          <DocumentList onSelect={setSelectedId} selectedId={selectedId} />
        </div>
      </aside>

      {/* Main Content */}
      <main className="flex-grow overflow-y-auto p-8">
        {selectedId ? (
          <DocumentView id={selectedId} />
        ) : (
          <div className="text-gray-500 text-lg">
            Select a document to view details
          </div>
        )}
      </main>
    </div>
  );
}
