import React, { useState } from 'react';
import { uploadDocument, uploadText } from "../services/api";

export default function UploadModal({ open, onClose }) {
  const [file, setFile] = useState(null);
  const [text, setText] = useState("");

  if (!open) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-40 backdrop-blur-md flex justify-center items-center z-50">
      <div className="bg-white dark:bg-gray-800 w-[600px] p-8 rounded-2xl shadow-xl relative">

        <button className="absolute right-4 top-4 text-xl" onClick={onClose}>Ã—</button>

        <h2 className="text-2xl font-bold mb-6">Add Sources</h2>

        {/* Upload file section */}
        <div className="border-2 border-gray-300 dark:border-gray-600 border-dashed p-6 rounded-xl text-center mb-6">
          <p className="mb-3 text-gray-600">Upload a PDF</p>
          <input 
            type="file" 
            onChange={(e) => setFile(e.target.files[0])}
            className="mx-auto"
          />
        </div>

        {/* Paste text section */}
        {/* <div className="mb-6">
          <label className="text-gray-600">Paste text</label>
          <textarea
            className="w-full border rounded-xl p-3 h-32 mt-2"
            placeholder="Paste document text here..."
            value={text}
            onChange={(e) => setText(e.target.value)}
          />
        </div> */}

        <button 
          className="bg-blue-600 text-white px-6 py-3 rounded-xl w-full hover:bg-blue-700"
          onClick={async () => {
            let notebookId = null;

            if (file) {
              const resp = await uploadDocument(file);
              notebookId = resp.id;
            }

            if (text.trim() !== "") {
              const resp = await uploadText(text);
              notebookId = resp.id;
            }

            if (notebookId) {
              window.location.href = `/notebook/${notebookId}`;
            }
          }}
        >
          Create Notebook
        </button>

      </div>
    </div>
  );
}
