import React from "react";
import LandingSection from "../components/LandingSection";
import Sidebar from "../components/Sidebar";
import Summary from "../components/Summary"; // your existing components

export default function NotebookPage({ selectedDoc }) {
  return (
    <div className="w-full overflow-x-hidden">

      {/* Landing / Intro */}
      <LandingSection />

      {/* Main App Section */}
      <div id="app-section" className="flex bg-gray-50 dark:bg-gray-900 min-h-screen">

        {/* Sidebar */}
        <Sidebar selectedDoc={selectedDoc} />

        {/* Main Content */}
        <main className="flex-grow p-10">
          <Summary selectedDoc={selectedDoc} />
        </main>

      </div>
    </div>
  );
}
