export default function App() {
  const [selectedId, setSelectedId] = useState(null);

  return (
    <div style={{ display: "grid", gridTemplateColumns: "300px 1fr", height: "100vh" }}>
      <div style={{ padding: 20, borderRight: "1px solid #ddd", overflowY: "auto" }}>
        <h2>AI Document Dashboard</h2>
        <UploadForm onUploaded={() => window.location.reload()} />
        <DocumentList onSelect={setSelectedId} />
      </div>

      <div style={{ overflowY: "auto" }}>
        <DocumentView id={selectedId} />
      </div>
    </div>
  );
}
