import React, { useEffect, useState } from "react";
import { getDocuments } from "../services/api";
import { useNavigate } from "react-router-dom";

export default function Home() {
    const [open, setOpen] = useState(false);
  const [notebooks, setNotebooks] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    loadNotebooks();
  }, []);

  const loadNotebooks = async () => {
    const data = await getDocuments();
    setNotebooks(data);
  };

  return (
    <div className="min-h-screen bg-gray-50 p-10">
      {/* Top Bar */}
      <div className="flex justify-between items-center mb-10">
        <h1 className="text-3xl font-bold flex items-center gap-2">
          <span className="text-blue-600 text-4xl">ðŸ“˜</span>
          NotebookLM â€“ Document Intelligence
        </h1>

        <button
          onClick={() => navigate("/create")}
          className="bg-black text-white px-5 py-3 rounded-xl hover:bg-gray-800 transition"
        >
          + Create New
        </button>
        <button 
          onClick={() => setOpen(true)}
          className="bg-black text-white px-6 py-3 rounded-xl hover:bg-gray-800 transition"
        >
          + Create New
        </button>
      </div>

      {/* Notebook Grid */}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 xl:grid-cols-4 gap-8">
        
        {notebooks.map((nb) => (
          <div
            key={nb.id}
            onClick={() => navigate(`/notebook/${nb.id}`)}
            className="cursor-pointer bg-white rounded-xl shadow-md p-5 border hover:shadow-xl transition h-[180px] flex flex-col justify-between"
          >
            <div>
              <h2 className="font-semibold text-lg line-clamp-2">
                {nb.title || "Untitled Document"}
              </h2>
              <p className="text-gray-500 text-sm mt-2">
                {new Date(nb.uploaded_at).toLocaleDateString()}
              </p>
            </div>

            <div>
              <span
                className={`text-xs px-2 py-1 rounded ${
                  nb.status === "done"
                    ? "bg-green-100 text-green-600"
                    : nb.status === "processing"
                    ? "bg-yellow-100 text-yellow-700"
                    : "bg-red-100 text-red-600"
                }`}
              >
                {nb.status}
              </span>
            </div>
          </div>
        ))}

      </div>
    </div>
  );
}
