import React, { useEffect, useState } from "react";
import { getDocument } from "../services/api";

export default function DocumentView({ id }) {
  const [doc, setDoc] = useState(null);
  useEffect(() => {
    if (!id) return;
    getDocument(id).then(setDoc);
  }, [id]);

  if (!doc) return <div>Select a document</div>;

  return (
    <div>
      <h2>{doc.title || doc.file}</h2>
      <p>Status: {doc.status}</p>

      {doc.extracted_text && (
        <div>
          <h3>Extracted Text</h3>
          {Object.entries(doc.extracted_text).map(([page, data]) => (
            <div key={page}>
              <h4>{page}</h4>
              <pre style={{whiteSpace:"pre-wrap"}}>{data.text}</pre>
            </div>
          ))}
        </div>
      )}

      {doc.key_values && (
        <div>
          <h3>Key Values</h3>
          <pre>{JSON.stringify(doc.key_values, null, 2)}</pre>
        </div>
      )}

      {doc.tables && doc.tables.length > 0 && (
        <div>
          <h3>Tables</h3>
          {doc.tables.map((t, i) => <pre key={i}>{JSON.stringify(t, null, 2)}</pre>)}
        </div>
      )}
    </div>
  );
}
