import React, { useEffect, useState } from "react";
import { getDocuments } from "../services/api";

const statusColors = {
  processing: "text-yellow-600 bg-yellow-100",
  done: "text-green-600 bg-green-100",
  failed: "text-red-600 bg-red-100",
};

export default function DocumentList({ onSelect, selectedId }) {
  const [docs, setDocs] = useState([]);

  useEffect(() => {
    getDocuments().then(setDocs);
  }, []);

  return (
    <div className="space-y-3">
      {docs.map((d) => (
        <div
          key={d.id}
          onClick={() => onSelect(d.id)}
          className={`p-3 rounded-lg border cursor-pointer ${
            selectedId === d.id ? "bg-blue-50 border-blue-500" : "bg-white"
          } hover:bg-blue-50 transition`}
        >
          <div className="font-medium">{d.title}</div>
          <span
            className={`inline-block mt-1 px-2 py-1 text-xs rounded ${statusColors[d.status]}`}
          >
            {d.status}
          </span>
        </div>
      ))}
    </div>
  );
}
