import React from "react";
import ThemeToggle from "./ThemeToggle";

export default function Sidebar({ selectedDoc }) {
  return (
    <aside className="w-[340px] bg-white dark:bg-gray-800 dark:text-gray-100 shadow-xl border-r dark:border-gray-700 p-6 flex flex-col">
      <h1 className="text-2xl font-bold mb-8 flex items-center gap-2">
        <span className="text-blue-600 text-3xl">ðŸ“˜</span>
       IntelliDocs
        <div className="ml-auto">
          <ThemeToggle />
        </div>
      </h1>

      <div className="flex-grow overflow-y-auto">
        <div className="font-semibold text-gray-500 mb-3">Notebook</div>
        
        <div className="p-4 rounded-xl bg-blue-50 dark:bg-blue-900 dark:text-blue-200 border border-blue-200 dark:border-blue-800 shadow-sm">
          <div className="font-medium text-md">{selectedDoc?.title}</div>

          <span className={`text-xs mt-2 inline-block px-2 py-1 rounded bg-green-100 text-green-600`}>
            {selectedDoc?.status}
          </span>
        </div>
      </div>
    </aside>
  );
}
