import React, { useState } from "react";
import { uploadDocument } from "../services/api";

export default function UploadForm({ onUploaded }) {
  const [file, setFile] = useState(null);
  const [title, setTitle] = useState("");

  const submit = async (e) => {
    e.preventDefault();
    if (!file) return alert("Choose a file");
    const result = await uploadDocument(file, title);
    onUploaded && onUploaded(result);
  };

  return (
    <form onSubmit={submit}>
      <input type="text" placeholder="Title (optional)" value={title} onChange={e=>setTitle(e.target.value)}/>
      <input type="file" accept=".pdf,image/*" onChange={e => setFile(e.target.files[0])}/>
      <button type="submit">Upload</button>
    </form>
  );
}
