import React, { useState } from 'react';
import UploadForm from "../components/UploadForm";

export default function Landing() {
  const [open, setOpen] = useState(false);

  return (
    <div className="min-h-screen bg-gray-50 px-16 py-12">
      <header className="flex justify-between items-center mb-12">
        <h1 className="text-3xl font-bold text-gray-800 flex gap-2">
          <span>ðŸ“˜</span> NotebookLM â€“ Document Intelligence
        </h1>
        <button 
          onClick={() => setOpen(true)}
          className="bg-black text-white px-6 py-3 rounded-xl hover:bg-gray-800 transition"
        >
          + Create New
        </button>
      </header>

      <UploadForm open={open} onClose={() => setOpen(false)} />

      <section className="grid grid-cols-4 gap-6">
        <div 
          onClick={() => setOpen(true)}
          className="border border-gray-300 bg-white p-8 rounded-2xl shadow-sm hover:shadow-md cursor-pointer flex flex-col justify-center items-center"
        >
          <div className="text-4xl mb-4">+</div>
          <div className="text-lg font-medium">Create new notebook</div>
        </div>

        {/* Future: show last notebooks as cards */}
      </section>
    </div>
  );
}
