import React, { useState } from "react";
import ChatPanel from "../components/ChatPanel";
export default function NotebookContent({ doc }) {
  const [tab, setTab] = useState("summary");

  const tabs = [
    { id: "summary", label: "Summary" },
    { id: "text", label: "Extracted Text" },
    { id: "pdf", label: "PDF Viewer" },
    { id: "raw", label: "Raw JSON" }
  ];

  return (
    <div>
            <div className="flex min-h-screen bg-gray-50">
        <Sidebar selectedDoc={doc} />

        <main className="flex-grow p-10 overflow-y-auto">
            {doc ? <NotebookContent doc={doc} /> : <div>Loading...</div>}
        </main>

        {/* Chat panel on right */}
        <ChatPanel docId={doc?.id} />
        </div>
      <h2 className="text-3xl font-bold mb-6">{doc.title}</h2>

      {/* Tabs */}
      <div className="flex border-b mb-8 space-x-8">
        {tabs.map((t) => (
          <button
            key={t.id}
            onClick={() => setTab(t.id)}
            className={`pb-3 text-lg ${
              tab === t.id
                ? "border-b-4 border-blue-600 text-blue-600"
                : "text-gray-500 hover:text-black"
            }`}
          >
            {t.label}
          </button>
        ))}
      </div>

      {/* Summary */}
      {tab === "summary" && (
        <div className="bg-white p-6 rounded-xl shadow-md">
          <h3 className="text-xl font-semibold mb-3">AI Summary</h3>
          <p className="whitespace-pre-wrap text-gray-700">{doc.summary || "No summary available."}</p>
        </div>
      )}

      {/* Extracted Text */}
      {tab === "text" && (
        <div className="space-y-6">
          {Object.keys(doc.extracted_text || {}).map((page) => (
            <div key={page} className="bg-white p-6 rounded-xl shadow-md">
              <h3 className="text-lg font-semibold mb-3">{page.replace("_", " ")}</h3>
              <p className="whitespace-pre-wrap text-gray-700">
                {doc.extracted_text[page].text}
              </p>
            </div>
          ))}
        </div>
      )}

      {/* Raw JSON */}
      {tab === "raw" && (
        <pre className="bg-gray-900 text-white p-6 rounded-xl overflow-x-auto shadow-md">
          {JSON.stringify(doc, null, 2)}
        </pre>
      )}

      {/* PDF Viewer */}
      {tab === "pdf" && (
        <iframe
          src={doc.file}
          title="PDF Viewer"
          className="w-full h-[85vh] rounded-xl shadow-lg"
        ></iframe>
      )}
    </div>
  );
}
