import React, { useEffect, useState } from "react";
import UploadForm from "../components/UploadForm";
import { getDocuments } from "../services/api";
import { useNavigate } from "react-router-dom";
import ThemeToggle from "../components/ThemeToggle";

export default function Home() {
    const [open, setOpen] = useState(false);
    const [showUpload, setShowUpload] = useState(false);

    const [notebooks, setNotebooks] = useState([]);
    const navigate = useNavigate();

  useEffect(() => {
    loadNotebooks();
  }, []);

  const loadNotebooks = async () => {
    const data = await getDocuments();
    setNotebooks(data);
  };

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900 p-10">
      {/* Top Bar */}
      <div className="flex justify-between items-center mb-10">
        <h1 className="text-3xl font-bold flex items-center gap-2 text-gray-800 dark:text-gray-100">
          <span className="text-blue-600 text-4xl">ðŸ“˜</span>
          IntelliDocs
        </h1>
        <div className="flex items-center gap-4">
          <ThemeToggle />
        
          {/* <button 
          onClick={() => setOpen(true)}
          className="bg-black text-white px-6 py-3 rounded-xl hover:bg-gray-800 transition"
        >
          + Create New
        </button> */}
        <button
          onClick={() => setShowUpload(true)}
          className="bg-black text-white px-5 py-3 rounded-xl"
        >
          + Create New
        </button>
        </div>
        <UploadForm open={open} onClose={() => setOpen(false)} />
      </div>

      {/* Notebook Grid */}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 xl:grid-cols-4 gap-8">
        
        {notebooks.map((nb) => (
          <div
            key={nb.id}
            onClick={() => navigate(`/notebook/${nb.id}`)}
            className="cursor-pointer bg-white dark:bg-gray-800 rounded-xl shadow-md p-5 border dark:border-gray-700 hover:shadow-xl transition h-[180px] flex flex-col justify-between"
          >
            <div>
              <h2 className="font-semibold text-lg line-clamp-2">
                {nb.title || "Untitled Document"}
              </h2>
              <p className="text-gray-500 text-sm mt-2">
                {new Date(nb.uploaded_at).toLocaleDateString()}
              </p>
            </div>
            {/* SHOW MODAL ONLY WHEN TRUE */}
      {showUpload && (
        <UploadModal
          onClose={() => setShowUpload(false)}
          onUploadFile={(file) => console.log(file)}
          onPasteText={() => console.log("Paste text")}
        />
      )}
            <div>
              <span
                className={`text-xs px-2 py-1 rounded ${
                  nb.status === "done"
                    ? "bg-green-100 text-green-600"
                    : nb.status === "processing"
                    ? "bg-yellow-100 text-yellow-700"
                    : "bg-red-100 text-red-600"
                }`}
              >
                {nb.status}
              </span>
            </div>
          </div>
        ))}

      </div>
    </div>
  );
}
