import React, { useEffect, useState } from "react";
import { getDocument } from "../services/api";

export default function DocumentView({ id }) {
  const [doc, setDoc] = useState(null);

  useEffect(() => {
    if (!id) return;
    getDocument(id).then(setDoc);
  }, [id]);

  if (!doc) return <div>Select a document</div>;

  return (
    <div style={{ padding: 20 }}>
      <h2>{doc.title}</h2>
      <p><b>Status:</b> {doc.status}</p>

      {doc.summary && (
        <>
          <h3>ðŸ“„ Summary</h3>
          <div style={{
            background: "#f7f7f7",
            padding: 15,
            borderRadius: 8,
            marginBottom: 20,
            whiteSpace: "pre-wrap"
          }}>
            {doc.summary}
          </div>
        </>
      )}

      <h3>Extracted Text</h3>
      {doc.extracted_text &&
        Object.entries(doc.extracted_text).map(([page, data]) => (
          <div key={page}>
            <h4>{page}</h4>
            <pre style={{ whiteSpace: "pre-wrap" }}>{data.text}</pre>
          </div>
        ))}
    </div>
  );
}
